import React from 'react'
import RecipeCard from '../Components/RecipeCard';
import Contexts from '../Contexts';
import { Row } from 'react-bootstrap';
import '../StyleSheets/HomePageStyle.css';
import HeroBannerRecipe from '../Components/HeroBannerRecipe';

export default function Recipes() {
    const API = "https://63e6e5dd15d793f46f87f0f3.mockapi.io/recipes";
    const [recipeList, setRecipeList] = useState([]);

    useEffect(() => {
      const refreshInfo = async () => {
        const response = await fetch(API);
        const data = await response.json();
        setRecipeList(data);
        console.log(data);
      }
      refreshInfo();
    }, []);

    const getRecipes = async () => {
      const response = await fetch(API);
      const data = await response.json();
      setRecipeList(data);
      console.log(data);
    };

    return (
        <div>
            <HeroBannerRecipe />
            <div className="homePageContainer">
                <div className="center">
                    <Row className="cards-container">
                        {recipeList.map(recipe => <RecipeCard recipe={recipe} key={recipe.id} />)}
                    </Row>
                </div>
            </div>
        </div>
    )
}
