import { Row } from 'react-bootstrap';
import { Link } from 'react-router-dom';
import '../StyleSheets/HomePageStyle.css';

export default function FeaturedRecipe({ recipeList }) {

    const [randomRecipe, setRandomRecipe] = useState({});

    const findRandom = () => {
        const index = (Math.floor(Math.random() * recipeList.length))
        setRandomRecipe(recipeList[index])
        console.log(index)
    }

    const [newestRecipe, setNewestRecipe] = useState({});
    const findNewest = () => {
        const index = (recipeList.length - 1);
        console.log(index)
