    }, []);

    return (
        <div>
            <button ref={buttonRef} onClick={findRandom}>RANDOM ME </button>
            <button onClick={findNewest}>CLICKE ME </button>
            <Row className="card-header">
                <Link to={"/recipes/" + randomRecipe.id} className="btn">
                    <img src={randomRecipe.image} alt="image of given recipe" className="recipe-image" />
                </Link>
                {console.log(randomRecipe)}
                <div className="card-text">
                    <div className="inline">
                        <h4> {randomRecipe.recipename} </h4>
                    </div>
                    <p className="contributor"> by {randomRecipe.contributor} </p>
                </div>
            </Row>
        </div>
    )
}
