import React, { useState, useEffect } from 'react'
import { useNavigate, useParams } from 'react-router-dom';
import HeroBannerIndividual from '../Components/HeroBannerIndividual';
import '../StyleSheets/RecipePage.css';
import Button from 'react-bootstrap/Button';
import Modal from 'react-bootstrap/Modal';
import { Form } from 'react-bootstrap';

export default function RecipePage({ recipeList, deleteRecipe, submitEditedRecipe, setId }) {
    let { recipeId } = useParams();
    const recipe = recipeList.find(r => r.id === recipeId);
    const [modalData, setModalData] = useState({});

    const [show, setShow] = useState(false);
    const handleClose = () => setShow(false);
    const handleShow = () => setShow(true);

    //destructuring the item/text input fields to be updated
    const [recipename, setRecipeName] = useState("");
    const [contributor, setContributor] = useState("");
    const [preptime, setPrepTime] = useState();
    const [totaltime, setTotalTime] = useState();
    const [ingredients, setIngredients] = useState([]);
    const [summary, setSummary] = useState("");
    const [steps, setSteps] = useState([]);

    //when submitted, creates an object called new book with specified attributes 
    /*     const [id, setId] = useState(""); */

    const [editedRecipe, setEditedRecipe] = useState({
        recipename: "",
        contributor: "",
        preptime: "",
        totaltime: "",
        ingredients: "",
        summary: "",
        steps: ""
    })

    const handleChange = (e) => {
        const { name, value } = e.target;
        setEditedRecipe((previousEntry) => {
            return { ...previousEntry, [name]: value }
        })
    };

    const handleEditedSubmit = () => {
/*         editedRecipe.ingredients = editedRecipe.ingredients.split(",").map(items => items.trim())
        editedRecipe.steps = editedRecipe.steps.split(",").map(items => items.trim()) */
        submitEditedRecipe(editedRecipe);
        console.log(recipeList)
        clearForms();
        navigateRecipes();
    }

    const clearForms = () => {
        setRecipeName("");
        setContributor("");
        setPrepTime("");
        setTotalTime("");
        setIngredients("");
        setSummary("");
        setSteps("");
    }

    let navigate = useNavigate();
    const navigateRecipes = () => {
        navigate("/recipes");
    };


    return (
        <div>
            <HeroBannerIndividual />
            <br /><br />
            <div className="recipe-page-container">
                <h1>Individual Recipe Page</h1>
                <p> {recipe.recipename} </p>
                <p> {recipe.contributor} </p>
                <button className="btn btn-info" onClick={() => {
                    handleShow();
                    setId(recipe.id);
                    setModalData(recipe);
                    { console.log(recipe.id) }
                }}>
                    EDIT recipe
                </button>

                <Modal show={show} onHide={handleClose}>
                    <Modal.Header closeButton>
                        <Modal.Title>Edit recipe </Modal.Title>
                    </Modal.Header>
                    <Modal.Body>
                        <Form>
                            <div className="class-inline mb-5">
                                <Form.Group className="col-6">
                                    <Form.Label>  Recipe Name + Restriction </Form.Label>
                                    <Form.Control
                                        type="text"
                                        placeholder={modalData.recipename}
                                        required
                                        onChange={(e) => handleChange(e)}
                                    />
                                </Form.Group>

                                <Form.Group className="col-5">
                                    <Form.Label> Contributor </Form.Label>
                                    <Form.Control
                                        type="text"
                                        placeholder={modalData.contributor}
                                        required
                                        onChange={(e) => handleChange(e)}
                                    />
                                </Form.Group>
                            </div>


                            <Form.Group className="mb-5">
                                <Form.Label> Recipe Summary </Form.Label>
                                <Form.Control
                                    as="textarea"
                                    cols={5}
                                    rows={2}
                                    placeholder={modalData.summary}
                                    required
                                    onChange={(e) => handleChange(e)}
                                />
                            </Form.Group>

                            <div className="class-inline-short">
                                <Form.Group className="col-4">
                                    <Form.Label>
                                        Prep &#x2022; minutes
                                    </Form.Label>
                                    <Form.Control
                                        type="number"
                                        placeholder={modalData.preptime}
                                        required
                                        onChange={(e) => handleChange(e)}
                                    />
                                    {/* <InputGroup.Text>Min</InputGroup.Text> */}
                                </Form.Group>

                                <Form.Group className="col-5 mb-3">
                                    <Form.Label>
                                        Total Time &#x2022; minutes
                                    </Form.Label>
                                    <Form.Control
                                        type="number"
                                        placeholder={modalData.totaltime}
                                        required
                                        onChange={(e) => handleChange(e)}
                                    />
                                </Form.Group>
                            </div>

                            <Form.Group className="mb-3">
                                <Form.Label> Ingredients &#x2022; Separate items by commas </Form.Label>
                                <Form.Control
                                    as="textarea"
                                    cols={10}
                                    rows={2}
                                    placeholder={modalData.ingredients}
                                    required
                                    onChange={(e) => handleChange(e)}
                                />
                            </Form.Group>
                            <br />

                            <br />
                            <Form.Group className="mb-3" controlid="recipeInput">
                                <Form.Label> Recipe Steps &#x2022; Separate items by commas </Form.Label>
                                <Form.Control
                                    as="textarea"
                                    cols={10}
                                    rows={4}
                                    placeholder={modalData.steps}
                                    required
                                    onChange={(e) => handleChange(e)}
                                />
                            </Form.Group>
                        </Form>
                    </Modal.Body>

                    <Modal.Footer>
                        <Button variant="secondary" onClick={handleClose}>
                            Close
                        </Button>
                        <Button variant="primary" onClick={() => {
                            handleEditedSubmit();
                            handleClose();
                        }}>
                            Save Changes
                        </Button>
                    </Modal.Footer>
                </Modal>

                <button
                    className="btn btn-warning"
                    onClick={() => {
                        deleteRecipe(recipe.id);
                        navigateRecipes();
                    }}>
                    DELETE Recipe
                </button>

                <div className="steps">

                </div>
            </div>
        </div >
    )
}
