import React, { useState, useEffect } from 'react'
import { useNavigate, useParams } from 'react-router-dom';
import HeroBannerIndividual from '../Components/HeroBannerIndividual';
import '../StyleSheets/RecipePage.css';
import Button from 'react-bootstrap/Button';
import Modal from 'react-bootstrap/Modal';

export default function RecipePage({ recipeList, deleteRecipe, submitEditedRecipe, setId }) {
    const API = "https://63e6e5dd15d793f46f87f0f3.mockapi.io/recipes";
    let { recipeId } = useParams();
    const recipe = recipeList.find(r => r.id === recipeId);

    const [show, setShow] = useState(false);
    const handleClose = () => setShow(false);
    const handleShow = () => setShow(true);

    /*     //destructuring the item/text input fields to be updated
        const [recipename, setRecipeName] = useState("");
        const [contributor, setContributor] = useState("");
        const [preptime, setPrepTime] = useState();
        const [totaltime, setTotalTime] = useState();
        const [ingredients, setIngredients] = useState([]);
        const [summary, setSummary] = useState("");
        const [steps, setSteps] = useState([]); */

    //when submitted, creates an object called new book with specified attributes 
    /*     const [id, setId] = useState(""); */

    const [editedRecipe, setEditedRecipe] = useState({
        recipename: "",
        contributor: "",
        preptime: "",
        totaltime: "",
        ingredients: [],
        summary: "",
        steps: []
    })

    const handleChange = (e) => {
        const { name, value } = e.target;
        setEditedRecipe((previousEntry) => {
            return { ...previousEntry, [name]: value }
        })
        console.log(editedRecipe.id)
    };

    const handleEditedSubmit = () => {
        submitEditedRecipe(editedRecipe);
    }

    let navigate = useNavigate();
    const navigateRecipes = () => {
        navigate("/recipes");
    };


    return (
        <div>
            <HeroBannerIndividual />
            <br /><br />
            <div className="recipe-page-container">
                <h1>Individual Recipe Page</h1>
                <p> {recipe.recipename} </p>
                <p> {recipe.contributor} </p>
                <button className="btn btn-info" onClick={handleShow}> EDIT recipe </button>

                <Modal show={show} onHide={handleClose}>
                    <Modal.Header closeButton>
                        <Modal.Title>Modal heading</Modal.Title>
                    </Modal.Header>
                    <Modal.Body>Woohoo, you're reading this text in a modal!</Modal.Body>
                    <Modal.Footer>
                        <Button variant="secondary" onClick={handleClose}>
                            Close
                        </Button>
                        <Button variant="primary" onClick={handleClose}>
                            Save Changes
                        </Button>
                    </Modal.Footer>
                </Modal>

                <button
                    className="btn btn-warning"
                    onClick={() => {
                        deleteRecipe(recipe.id);
                        navigateRecipes();
                    }}>
                    DELETE Recipe
                </button>

                <div className="steps">

                </div>
            </div>
        </div >
    )
}
