import React, { useState, useEffect, useRef } from 'react'
import { Row } from 'react-bootstrap';
import { Link } from 'react-router-dom';
import '../StyleSheets/HomePageStyle.css';

export default function FeaturedRecipe({ recipeList }) {

    const [newestRecipe, setNewestRecipe] = useState({});
    const [index, setIndex] = useState(0);

    const findNewest = () => {
        setIndex(recipeList.length - 1);
        setNewestRecipe(recipeList[index])
        console.log(newestRecipe);
    }

    const changeNavColor = () => {
        if (window.scrollY >= 80) {
            setNavColor(true);
        } else {
            setNavColor(false);
        }
    }
    //event listener for window to watch scrolling and apply data to changeNavColor function
    window.addEventListener("scroll", changeNavColor);
    return (
        <div>
            <Row className="card-header">
                <div className="card-text">
                    <div className="inline">
                        <h4> {newestRecipe.recipename} </h4>
                    </div>
                    <p className="contributor"> by {newestRecipe.contributor} </p>
                </div>
                <Link to={"/recipes/" + newestRecipe.id} className="btn">
                    <img src={newestRecipe.image} alt="image of given recipe" className="recipe-image" />
                </Link>
            </Row>
        </div>
    )
}
