import { Routes, Route } from 'react-router-dom';
import Homepage from './Pages/Homepage';
import Recipes from './Pages/RecipesPage';
import NewRecipe from './Pages/NewRecipe';
import About from './Pages/About';
import NavigationBar from './Components/NavigationBar';
import RecipePage from './Pages/RecipePage';
import { useState, useEffect } from 'react';

export default function App() {
  const API = "https://63e6e5dd15d793f46f87f0f3.mockapi.io/recipes";
  const [recipeList, setRecipeList] = useState([]);
  const [search, setSearch] = useState("");

  useEffect(() => {
    const refreshInfo = async () => {
      const response = await fetch(API);
      const data = await response.json();
      setRecipeList(data);
      console.log(data);
    }
    refreshInfo();
  }, []);

  const getRecipes = async () => {
    const response = await fetch(API);
    const data = await response.json();
    setRecipeList(data);
    console.log(data);
  };

  const addRecipeClick = async (newRecipe) => {
    await setRecipeList(recipeList.concat(newRecipe))
    await fetch(API, {
      method: "POST",
      headers: { "Content-type": "application/json" },
    })
    console.log(recipeList);
    getRecipes();
  }

  const editRecipe = async (recipeId) => {
    let resourceID = (recipeId);
    await fetch(API + "/" + resourceID, {
      headers: { "Content-type": "application/json" },
      body: JSON.stringify()
    })
    console.log(recipeList);
    getRecipes();
  }

  const deleteRecipe = async (recipeId) => {
    const resourceID = (recipeId);
    const response = await fetch(
      API + "/" + resourceID,
      { method: "DELETE" }
    )
    console.log("The recipe has been removed");
    getRecipes();
  }

  return (
    <div className="App">
      <NavigationBar />
      <Routes>
        <Route path="/" element={<Homepage recipeList={recipeList} />}></Route>
        <Route path="/recipes" element={<Recipes recipeList={recipeList} setSearch={setSearch} />}></Route>
        <Route path="/about" element={<About />}></Route>
        <Route path="/new" element={<NewRecipe addRecipeClick={addRecipeClick} />}></Route>
        <Route path="recipes/:recipeId" element={<RecipePage recipeList={recipeList} />} />
      </Routes>
    </div >
  );
}
