import React from 'react'
import HeroBannerHome from '../Components/HeroBanner';
import RecipeCard from '../Components/RecipeCard';
import '../StyleSheets/HomePageStyle.css';
import { Row } from 'react-bootstrap';
import FeaturedRecipe from '../Components/FeaturedRecipe';

export default function Homepage({ recipeList, API, newestRecipe }) {

    return (
        <div>
            <HeroBannerHome />
            <div className="featured-container">
                <div className="featured-header">
                    CHECK OUT OUR NEWEST RECIPE
                </div>
                <FeaturedRecipe recipeList={recipeList} API={API} newestRecipe={featuredRecipe} />
            </div>
        </div>
    )
}

