export default function FeaturedRecipe({ recipeList }) {

    useEffect(() => {
        const getRandomRecipe = (recipeList) => {
            const randomIndex = Math.floor(Math.random() * recipeList.length);
            const randomRecipe = recipeList[randomIndex];
            return randomRecipe;
        }
        getRandomRecipe(); 
    }, [])


    return (
        <div>
            {console.log(randomRecipe)}
        </div>
    )
}
