import React, { useState } from 'react'
import Form from 'react-bootstrap/Form';
import { Button } from 'react-bootstrap';

export default function AddRecipeButton({ addRecipeClick }) {

    //destructuring the item/text input fields to be updated
    const [recipeName, setRecipeName] = useState("");
    const [contributor, setContributor] = useState("");
    const [prepTime, setPrepTime] = useState();
    const [totalTime, setTotalTime] = useState();
    const [ingredients, setIngredients] = useState("");
    const [summary, setSummary] = useState("");
    const [steps, setSteps] = useState("");

    //when submitted, creates an object called new book with specified attributes 
    const handleSubmit = () => {
        const newRecipe = { recipeName, contributor, prepTime, ingredients, summary, steps };
        addRecipeClick(newRecipe);
        console.log(newRecipe);
        clearForms();
    }

    const clearForms = () => {
        setRecipeName("");
        setContributor("");
        setPrepTime("");
        setIngredients("");
        setSummary("");
        setSteps("");
    }

    return (
        <div>
            <h1> Contribute your own recipe </h1>
            <Form>
                <Form.Group className="mb-3" controlId="formTitleInput">
                    <Form.Label> Restriction + Recipe Name </Form.Label>
                    <Form.Control
                        type="text"
                        placeholder="ex. GF Teriyaki Chicken"
                        required
                        value={recipeName}
                        onChange={(e) => setRecipeName(e.target.value)}
                    />
                </Form.Group>

                <Form.Group className="mb-3" controlId="contributorInput">
                    <Form.Label> Contributor </Form.Label>
                    <Form.Control
                        type="text"
                        placeholder="Your name"
                        requiredvalue={contributor}
                        onChange={(e) => setContributor(e.target.value)}
                    />
                </Form.Group>

                <Form.Group className="mb-3" controlId="prepTimeInput">
                    <Form.Label> Prep time </Form.Label>
                    <Form.Control
                        type="number"
                        placeholder="enter total prep time"
                        required
                        value={prepTime}
                        onChange={(e) => setPrepTime(e.target.value)}
                    />
                    <p>Minutes</p>
                </Form.Group>

                <Form.Group className="mb-3" controlId="prepTimeInput">
                    <Form.Label> Total Recipe Time </Form.Label>
                    <Form.Control
                        type="number"
                        placeholder="enter total prep time"
                        required
                        value={totalTime}
                        onChange={(e) => setTotalTime(e.target.value)}
                    />
                    <p>Minutes</p>
                </Form.Group>

                <Form.Group className="col-3" controlId="ingredientsInput">
                    <Form.Label> Ingredients </Form.Label>
                    <Form.Control
                        type="text"
                        placeholder="Write a brief summary of what this dish is!"
                        required
                        value={ingredients}
                        onChange={(e) => setIngredients(e.target.value)}
                    />
                </Form.Group>

                <Form.Group className="mb-3" controlId="ingredientsInput">
                    <Form.Label> Recipe Step-by-step </Form.Label>
                    <Form.Control
                        as="textarea"
                        cols={10}
                        rows={4}
                        placeholder="Write the steps to the recipe"
                        required
                        value={steps}
                        onChange={(e) => setSteps(e.target.value)}
                    />
                </Form.Group>

                <Button
                    className="submit-btn"
                    variant="success"
                    onClick={() => {
                        handleSubmit();
                    }}>
                    Submit Recipe
                </Button>
            </Form>
        </div>
    )
}
