import React, { useState } from 'react'
import Form from 'react-bootstrap/Form';
import { Button, InputGroup } from 'react-bootstrap';
import '../StyleSheets/AddRecipeFormStyle.css';

export default function AddRecipeButton({ addRecipeClick }) {

    //destructuring the item/text input fields to be updated
    const [recipeName, setRecipeName] = useState("");
    const [contributor, setContributor] = useState("");
    const [prepTime, setPrepTime] = useState();
    const [totalTime, setTotalTime] = useState();
    const [ingredients, setIngredients] = useState("");
    const [summary, setSummary] = useState("");
    const [steps, setSteps] = useState("");
    const [multipleVal, setMultipleVal] = useState("");


    const handleValAdd = () => {
        const x = [...val, []]
        setMultipleVal(x);
    }

    const handleValChange = (onChangeVal, index) => {
        const inputData = [...val]
        inputData[i] = onChangeVal.target.value;
        setMultipleVal(inputData);
    }



    //when submitted, creates an object called new book with specified attributes 
    const handleSubmit = () => {
        const newRecipe = { recipeName, contributor, prepTime, ingredients, summary, steps };
        addRecipeClick(newRecipe);
        console.log(newRecipe);
        clearForms();
    }

    const clearForms = () => {
        setRecipeName("");
        setContributor("");
        setPrepTime("");
        setTotalTime("");
        setIngredients("");
        setSummary("");
        setSteps("");
    }

    return (
        <div className="recipe-forms">
            <Form>
                <div className="class-inline">
                    <Form.Group className="col-6" controlId="recipeNameInput">
                        <Form.Label>  Recipe Name + Restriction </Form.Label>
                        <Form.Control
                            type="text"
                            placeholder="ex. Teriyaki Chicken (GF)"
                            required
                            value={recipeName}
                            onChange={(e) => setRecipeName(e.target.value)}
                        />
                    </Form.Group>

                    <Form.Group className="col-5" controlId="contributorInput">
                        <Form.Label> Contributor </Form.Label>
                        <Form.Control
                            type="text"
                            placeholder="Your name"
                            requiredvalue={contributor}
                            onChange={(e) => setContributor(e.target.value)}
                        />
                    </Form.Group>
                </div>


                <Form.Group className="mb-5" controlId="ingredientsInput">
                    <Form.Label> Recipe Summary </Form.Label>
                    <Form.Control
                        as="textarea"
                        cols={5}
                        rows={3}
                        placeholder="Write a brief summary of what this dish is!"
                        required
                        value={summary}
                        onChange={(e) => setSummary(e.target.value)}
                    />
                </Form.Group>

                <div className="class-inline-short">
                    <Form.Group className="col-4">
                        <Form.Label>
                            PrepTime
                        </Form.Label>
                        <InputGroup>
                            <Form.Control
                                type="text"
                                placeholder=""
                                required
                                value={prepTime}
                                onChange={(e) => setPrepTime(e.target.value)}
                            />
                            <InputGroup.Text>Min</InputGroup.Text>
                        </InputGroup>
                    </Form.Group>


                    <Form.Group className="col-4" controlId="totalTimeInput">
                        <Form.Label>
                            PrepTime
                        </Form.Label>
                        <InputGroup>
                            <Form.Control
                                type="text"
                                placeholder=""
                                required
                                value={totalTime}
                                onChange={(e) => setTotalTime(e.target.value)}
                            />
                            <InputGroup.Text>Min</InputGroup.Text>
                        </InputGroup>
                    </Form.Group>
                </div>

                <Form.Group className="mb-3" controlId="ingredientsInput">
                    <Form.Label> Ingredients </Form.Label>
                    <Form.Control
                        type="text"
                        placeholder="ex. 2 cups GF flour"
                        required
                        value={ingredients}
                        onChange={(e) => setIngredients(e.target.value)}
                    />
                </Form.Group>
                <br />

                <Form.Group className="mb-3" controlId="recipeInput">
                    <Form.Label> Recipe Step-by-step </Form.Label>
                    <Form.Control
                        as="textarea"
                        cols={10}
                        rows={4}
                        placeholder="Write the steps to the recipe"
                        required
                        value={steps}
                        onChange={(e) => setSteps(e.target.value)}
                    />
                </Form.Group>
                <br />

                <Button
                    className="submit-btn"
                    variant="success"
                    onClick={() => {
                        handleSubmit();
                    }}>
                    Submit Recipe
                </Button>
            </Form >
        </div >
    )
}
