export default function FeaturedRecipe({ recipeList }) {

    const [randomIndex, setRandomIndex] = useState();
    const [randomRecipe, setRandomRecipe] = useState({});

    const getRandomRecipe = async () => {
        csetRandomIndex(Math.floor(Math.random() * recipeList.length))
        setRandomRecipe(recipeList[randomIndex])
        return randomRecipe;
    }


    return (
        <div>
            <button onClick={getRandomRecipe}> CLICK ME</button>
        </div>
    )
}
