import React, { useState } from 'react'
import Container from 'react-bootstrap/Container';
import Nav from 'react-bootstrap/Nav';
import Navbar from 'react-bootstrap/Navbar';
import { NavLink } from 'react-router-dom';
import '../StyleSheets/NavStyle.css';

export default function NavigationBar() {
    /*     const [color, setColor] = useState(false);

        const changeColor = () => {
            if (window.scrollY >= 90) {
                setColor(true);
            } else {
                setColor(false);
            }
        }

        window.addEventListener('scroll', changeColor) */
    const navColorChange = document.querySelector('.navbar');
    window.addEventListener('scroll', () => {
        if (window.scrollY > 90) {
            navColorChange.classList.add('navbar-scrolled');
        } else if (window.scrollY < 90) {
            navColorChange.classList.remove('navbar-scrolled');
        }
    });

    return (
        <div>
            <Navbar expand="lg" className="fixed-top" variant="dark">
                <Container className="links">
                    <Navbar.Brand as={NavLink} to="/">React-Bootstrap</Navbar.Brand>
                    <Navbar.Toggle aria-controls="basic-navbar-nav" />
                    <Navbar.Collapse id="basic-navbar-nav">

                        <Nav className="ms-auto">
                            <Nav.Link as={NavLink} to="/recipes">Recipes</Nav.Link>
                            <Nav.Link as={NavLink} to="/about">About</Nav.Link>
                            <Nav.Link as={NavLink} to="/new">Add a Recipe</Nav.Link>
                        </Nav>
                    </Navbar.Collapse>
                </Container>
            </Navbar>
        </div>
    )
}
