import React, { useState } from 'react'
import Container from 'react-bootstrap/Container';
import Nav from 'react-bootstrap/Nav';
import Navbar from 'react-bootstrap/Navbar';
import { NavLink } from 'react-router-dom';
import '../StyleSheets/NavStyle.css';

export default function NavigationBar() {

    const navColorChange = document.querySelector('.navbar');
    window.addEventListener('scroll', () => {
        if (window.scrollY > 90) {
            navColorChange.classList.add('navbar-scrolled');
        } else if (window.scrollY < 90) {
            navColorChange.classList.remove('navbar-scrolled');
        }
    });

    const navTextColorChange = document.querySelector('.nav-link');
    window.addEventListener('scroll' , () => {
        if (window.scrollY > 90) {
            navTextColorChange.classList.add('')
        }
    })

    return (
        <div>
            <Navbar expand="lg" className="fixed-top">
                <Container className="links">
                    <Navbar.Brand as={NavLink} to="/">React-Bootstrap</Navbar.Brand>
                    <Navbar.Toggle aria-controls="basic-navbar-nav" />
                    <Navbar.Collapse id="basic-navbar-nav">

                        <Nav className="ms-auto">
                            <Nav.Link as={NavLink} to="/recipes" className="link-text">Recipes</Nav.Link>
                            <Nav.Link as={NavLink} to="/about" className="link-text">About</Nav.Link>
                            <Nav.Link as={NavLink} to="/new" className="link-text">Add a Recipe</Nav.Link>
                        </Nav>
                    </Navbar.Collapse>
                </Container>
            </Navbar>
        </div>
    )
}
