import React, { useState, useEffect } from 'react'
import { Row } from 'react-bootstrap';
import { Link } from 'react-router-dom';
import '../StyleSheets/HomePageStyle.css';

export default function FeaturedRecipe({ recipeList }) {

    const [randomIndex, setRandomIndex] = useState(null);
    const [randomRecipe, setRandomRecipe] = useState({});

    useEffect(() => {
        const findRandom = async () => {
            const index = (Math.floor(Math.random() * recipeList.length))
            await setRandomIndex(index)
            await setRandomRecipe(recipeList[randomIndex])
            console.log(randomIndex)
        }
        findRandom();
    }, []);

    return (
        <div>
            <Row className="card-header">
                <Link to={"/recipes/" + randomRecipe.id} className="btn">
                    <img src={randomRecipe.image} alt="image of given recipe" className="recipe-image" />
                </Link>
                <div className="card-text">
                    <div className="inline">
                        <h4> {randomRecipe.recipename} </h4>
                    </div>
                    <p className="contributor"> by {randomRecipe.contributor} </p>
                </div>
            </Row>
        </div>
    )
}
