import React from 'react'
import ReactPlayer from 'react-player';
import heroVideo from '../Assets/heroVideo.mp4';
import '../StyleSheets/Hero'

export default function HeroBanner() {
    return (
        <div className="hero">
            <ReactPlayer
                url={heroVideo}
                playing
                loop
                muted
                width="100%"
                height="100%"
            />
        </div>
    )
}
