import React from 'react'
import Container from 'react-bootstrap/Container';
import Nav from 'react-bootstrap/Nav';
import Navbar from 'react-bootstrap/Navbar';
import { Link, Route, Routes, NavLink } from 'react-router-dom';
import '../StyleSheets/NavStyle.css';
import { useState } from 'react';

export default function NavigationBar() {
    const [navbar, setNavbar] = useState(false);

    const changeNavColor = () => {
        console.log(window.scrollY);
        if (window.scrollY >= 80) {
            setNavbar(true);
        } else {
            setNavbar(false);
        }
    };

    window.addEventListener('scroll', changeNavColor);

    return (
        <div>
            <Navbar expand="lg" className="fixed-top" variant="dark">
                <Container className="links">
                    <Navbar.Brand as={NavLink} to="/">React-Bootstrap</Navbar.Brand>
                    <Navbar.Toggle aria-controls="basic-navbar-nav" />
                    <Navbar.Collapse id="basic-navbar-nav">

                        <Nav className="ms-auto">
                            <Nav.Link as={NavLink} to="/recipes">Recipes</Nav.Link>
                            <Nav.Link as={NavLink} to="/about">About</Nav.Link>
                            <Nav.Link as={NavLink} to="/contribute">Contribute</Nav.Link>
                        </Nav>

                    </Navbar.Collapse>
                </Container>
            </Navbar>
        </div>
    )
}
