import React, { useState, useEffect } from 'react'
import { useNavigate, useParams } from 'react-router-dom';
import HeroBannerIndividual from '../Components/HeroBannerIndividual';
import '../StyleSheets/RecipePage.css';
import Button from 'react-bootstrap/Button';
import Modal from 'react-bootstrap/Modal';
import { Form } from 'react-bootstrap';

export default function RecipePage({ recipeList, deleteRecipe, API, getRecipes /* submitEditedRecipe, setId  */ }) {
    let { recipeId } = useParams();
    const recipe = recipeList.find(r => r.id === recipeId);
    const [modalData, setModalData] = useState({});
    const [id, setId] = useState("");

    const [show, setShow] = useState(false);
    const handleClose = () => setShow(false);
    const handleShow = () => setShow(true);

    const [editedRecipe, setEditedRecipe] = useState({
        recipename: "",
        contributor: "",
        preptime: "",
        totaltime: "",
        ingredients: "",
        summary: "",
        steps: "",
        image: ""
    })

    const handleChange = (e) => {
        const { name, value } = e.target;
        setEditedRecipe((previousEntry) => {
            return { ...previousEntry, [name]: value }
        })
    };

    const submitEditedRecipe = async (editedRecipe) => {
        editedRecipe.ingredients = editedRecipe.ingredients.split(",").map(items => items.trim())
        editedRecipe.steps = editedRecipe.steps.split(",").map(items => items.trim())

        await fetch(API + "/" + id,
            {
                method: "PUT",
                body: JSON.stringify(editedRecipe),
                headers: { "Content-Type": "application/json" },
            })
        console.log(recipeList);
        getRecipes();
    }

    const handleEditedSubmit = () => {
        submitEditedRecipe(editedRecipe);
        console.log(recipeList)
        navigateRecipes();
    }

    let navigate = useNavigate();
    const navigateRecipes = () => {
        navigate("/recipes");
    };


    return (
        <div>
            <HeroBannerIndividual />
            <br /><br />
            <div className="recipe-page-container">
                <h1> {recipe.recipename} </h1>
                <img src={recipe.image} alt="image of given recipe" className="recipe-image" />
                <p> {recipe.summary} </p>
                <p> {recipe.contributor} </p>
                <button className="btn btn-info" onClick={() => {
                    handleShow();
                    setId(recipe.id);
/*                     setModalData(recipe); */
                }}>
                    EDIT recipe
                </button>

                <Modal show={show} onHide={handleClose}>
                    <Modal.Header closeButton>
                        <Modal.Title>Edit recipe </Modal.Title>
                    </Modal.Header>
                    <Modal.Body>
                        <Form>
                            <div className="class-inline mb-5">
                                <Form.Group className="col-6">
                                    <Form.Label>  Recipe Name + Restriction </Form.Label>
                                    <Form.Control
                                        name="recipename"
                                        type="text"
                                        placeholder={recipe.recipename}
                                        required
                                        defaultValue={recipe.recipename}
                                        onChange={(e) => handleChange(e)}
                                    />
                                </Form.Group>

                                <Form.Group className="col-5">
                                    <Form.Label> Contributor </Form.Label>
                                    <Form.Control
                                        name="contributor"
                                        type="text"
                                        placeholder={recipe.contributor}
                                        required
                                        defaultValue={modalData.contributor}
                                        onChange={(e) => handleChange(e)}
                                    />
                                </Form.Group>
                            </div>

                            <Form.Group className="mb-5">
                                <Form.Label> Recipe Image - horizontal images only! </Form.Label>
                                <Form.Control
                                    name="image"
                                    type="text"
                                    placeholder="Paste in an image URL for your recipe"
                                    required
                                    defaultValue={modalData.image}
                                    onChange={(e) => handleChange(e)}
                                />
                            </Form.Group>

                            <Form.Group className="mb-5">
                                <Form.Label> Recipe Summary </Form.Label>
                                <Form.Control
                                    name="summary"
                                    as="textarea"
                                    cols={5}
                                    rows={2}
                                    placeholder={modalData.summary}
                                    required
                                    defaultValue={modalData.summary}
                                    onChange={(e) => handleChange(e)}
                                />
                            </Form.Group>

                            <div className="class-inline-short">
                                <Form.Group className="col-4">
                                    <Form.Label>
                                        Prep &#x2022; minutes
                                    </Form.Label>
                                    <Form.Control
                                        name="preptime"
                                        type="number"
                                        placeholder={modalData.preptime}
                                        required
                                        defaultValue={modalData.preptime}
                                        onChange={(e) => handleChange(e)}
                                    />
                                    {/* <InputGroup.Text>Min</InputGroup.Text> */}
                                </Form.Group>

                                <Form.Group className="col-5 mb-3">
                                    <Form.Label>
                                        Total Time &#x2022; minutes
                                    </Form.Label>
                                    <Form.Control
                                        name="totaltime"
                                        type="number"
                                        placeholder={modalData.totaltime}
                                        required
                                        defaultValue={modalData.totaltime}
                                        onChange={(e) => handleChange(e)}
                                    />
                                </Form.Group>
                            </div>

                            <Form.Group className="mb-3">
                                <Form.Label> Ingredients &#x2022; Separate items by commas </Form.Label>
                                <Form.Control
                                    name="ingredients"
                                    as="textarea"
                                    cols={10}
                                    rows={2}
                                    placeholder={modalData.ingredients}
                                    required
                                    onChange={(e) => handleChange(e)}
                                    defaultValue={modalData.ingredients}
                                />
                            </Form.Group>
                            <br />

                            <br />
                            <Form.Group className="mb-3" controlid="recipeInput">
                                <Form.Label> Recipe Steps &#x2022; Separate items by commas </Form.Label>
                                <Form.Control
                                    name="steps"
                                    as="textarea"
                                    cols={10}
                                    rows={4}
                                    placeholder={modalData.steps}
                                    required
                                    defaultValue={modalData.steps}
                                    onChange={(e) => handleChange(e)}
                                />
                            </Form.Group>
                        </Form>
                    </Modal.Body>

                    <Modal.Footer>
                        <Button variant="secondary" onClick={handleClose}>
                            Close
                        </Button>
                        <Button variant="primary" onClick={() => {
                            handleEditedSubmit();
                            handleClose();
                        }}>
                            Save Changes
                        </Button>
                    </Modal.Footer>
                </Modal>

                <button
                    className="btn btn-warning"
                    onClick={() => {
                        deleteRecipe(recipe.id);
                        navigateRecipes();
                    }}>
                    DELETE Recipe
                </button>

                <div className="steps">

                </div>
            </div>
        </div >
    )
}
