import React, { useState, useEffect } from 'react'
import { useNavigate, useParams } from 'react-router-dom';
import HeroBannerIndividual from '../Components/HeroBannerIndividual';
import '../StyleSheets/RecipePage.css';

export default function RecipePage({ recipeList, deleteRecipe }) {
    let { recipeId } = useParams();
    const recipe = recipeList.find(r => r.id === recipeId);

    /*     let navigate = useNavigate();
        const routeChange = () => {
            let path = " /recipes/ ";
            navigate(path);
        } */


    let navigate = useNavigate();
    const navigateHome = () => {
        navigate("/");
    };

    const navigateRecipes = () => {
        navigate("/");
    };


    return (
        <div>
            <HeroBannerIndividual />
            <br /><br />
            <div className="recipe-page-container">
                <h1>Individual Recipe Page</h1>
                <p> {recipe.recipename} </p>
                <p> {recipe.contributor} </p>
                <button className="btn btn-info"> EDIT recipe </button>

                <button
                    className="btn btn-warning"
                    onClick={() => {
                        deleteRecipe(recipe.id);
                        navigateHome();
                    }}>
                    DELETE Recipe
                </button>
            </div>
        </div >
    )
}
